import React, { Component } from "react";
import { withFormik } from "formik";
import * as yup from "yup";
import Grid from "@material-ui/core/Grid";
import TextField from "@material-ui/core/TextField";
import DateFnsUtils from "@date-io/date-fns";
import {
  MuiPickersUtilsProvider,
  KeyboardDatePicker
} from "@material-ui/pickers";
import { Typography, Divider, Button, withStyles } from "@material-ui/core";
import Radio from "@material-ui/core/Radio";
import RadioGroup from "@material-ui/core/RadioGroup";
import FormControlLabel from "@material-ui/core/FormControlLabel";
import FormControl from "@material-ui/core/FormControl";
import FormLabel from "@material-ui/core/FormLabel";
import Checkbox from "@material-ui/core/Checkbox";
import { PhoneRegExp, ZipCodeRegExp, AdhharRegExp } from "../utils/constants";
const useStyles = theme => ({
  titleContainer: {
    [theme.breakpoints.up("sm")]: {
      paddingLeft: theme.spacing(6),
      paddingRight: theme.spacing(5)
    },
    [theme.breakpoints.down("sm")]: {
      paddingLeft: theme.spacing(2),
      paddingRight: theme.spacing(2)
    },
    textAlign: "left"
  },
  row: {
    [theme.breakpoints.up("sm")]: {
      paddingLeft: theme.spacing(6),
      paddingRight: theme.spacing(6)
    }
  },
  paddingRight_16: {
    [theme.breakpoints.up("sm")]: {
      paddingRight: theme.spacing(2)
    }
  }
});

class MemberForm extends Component {
  constructor(props) {
    super(props);
    this.state = {
      selectedDate: new Date()
    };
  }

  removeOrResetMember = () => {
    const {
      parentIndex,
      formIndex,
      deleteMemberDataToMemberList,
      resetForm
    } = this.props;
    if (formIndex === 0) resetForm();
    else
      parentIndex !== undefined
        ? deleteMemberDataToMemberList(parentIndex, formIndex)
        : deleteMemberDataToMemberList(formIndex);
  };
  render() {
    console.log(this.props);
    const {
      parentIndex,
      dataObj,
      formIndex,
      dropDownListData,
      classes,
      theme,
      values,
      touched,
      errors,
      dirty,
      isSubmitting,
      handleChange,
      handleBlur,
      handleSubmit
    } = this.props;
    const { selectedDate } = this.state;
    return (
      <form autoComplete="off" onSubmit={handleSubmit} id={`form_${formIndex}`}>
        <Grid container alignItems="center">
          <Grid item xs={12}>
            <Grid container alignItems="center">
              <Grid item xs={12} className={classes.titleContainer}>
                <Typography gutterBottom color="primary">
                  Members Details
                </Typography>
                <Divider />
              </Grid>
              <Grid
                item
                xs={12}
                style={{ paddingTop: "8px", paddingBottom: "8px" }}
              >
                <Grid
                  container
                  // spacing={3}

                  className={classes.row}
                  justify="space-evenly"
                >
                  <Grid item sm={3} xs={11} className={classes.paddingRight_16}>
                    <TextField
                      fullWidth
                      id="firstName"
                      label="FIRST NAME / પ્રથમ નામ"
                      value={values.firstName}
                      onChange={handleChange}
                      onBlur={handleBlur}
                      error={!!touched.firstName && !!errors.firstName}
                      helperText={touched.firstName && errors.firstName}
                    />
                  </Grid>
                  <Grid item sm={3} xs={11} className={classes.paddingRight_16}>
                    <TextField
                      fullWidth
                      id="middleName"
                      label="MIDDLE NAME / પિતાનું નામ"
                      value={values.middleName}
                      onChange={handleChange}
                      onBlur={handleBlur}
                      error={!!touched.middleName && !!errors.middleName}
                      helperText={touched.middleName && errors.middleName}
                    />
                  </Grid>
                  <Grid item sm={3} xs={11} className={classes.paddingRight_16}>
                    <TextField
                      fullWidth
                      id="lastName"
                      label="LAST NAME / છેલ્લું નામ"
                      value={values.lastName}
                      onChange={handleChange}
                      onBlur={handleBlur}
                      error={!!touched.lastName && !!errors.lastName}
                      helperText={touched.lastName && errors.lastName}
                    />
                  </Grid>
                  <Grid item sm={3} xs={11}>
                    <TextField
                      fullWidth
                      id="relation"
                      select
                      label="RELATION / સંબંધ"
                      value={values.relation}
                      onChange={handleChange}
                      onBlur={handleBlur}
                      error={!!touched.relation && !!errors.relation}
                      helperText={touched.relation && errors.relation}
                      SelectProps={{
                        native: true
                      }}
                    >
                      <option key={0} disabled selected />
                      {dropDownListData.relationList &&
                        dropDownListData.relationList.length &&
                        dropDownListData.relationList.map(option => (
                          <option key={option.Id} value={option.Id}>
                            {option.Name}
                          </option>
                        ))}
                    </TextField>
                  </Grid>
                </Grid>
              </Grid>
              <Grid
                item
                xs={12}
                style={{ paddingTop: "8px", paddingBottom: "8px" }}
              >
                <Grid container className={classes.row} >
                  <Grid item sm={3} xs={11} className={classes.paddingRight_16}>
                    <MuiPickersUtilsProvider utils={DateFnsUtils}>
                      <KeyboardDatePicker
                        fullWidth
                        id="dateOfBirth"
                        format="MM/dd/yyyy"
                        label="DATE OF BIRTH / જન્મ તારીખ"
                        KeyboardButtonProps={{
                          "aria-label": "change date"
                        }}
                        value={values.dateOfBirth}
                        onChange={(event, value) => {
                          this.props.setFieldValue(
                            "dateOfBirth",
                            new Date(value)
                          );
                        }}
                        onBlur={handleBlur}
                        error={!!touched.dateOfBirth && !!errors.dateOfBirth}
                        helperText={touched.dateOfBirth && errors.dateOfBirth}
                      />
                    </MuiPickersUtilsProvider>
                  </Grid>
                  <Grid item sm={3} xs={11} className={classes.paddingRight_16}>
                    <FormControl
                      required
                      style={{ alignItems: "flex-start", float: "left" }}
                    >
                      <FormLabel>Gender</FormLabel>
                      {/* import FormHelperText from '@material-ui/core/FormHelperText'; */}
                      <RadioGroup
                        aria-label="GENDER / લિંગ"
                        id="gender"
                        style={{ flexDirection: "row" }}
                        value={values.gender}
                        onChange={eve => {
                          this.props.setFieldValue("gender", eve.target.value);
                        }}
                        // onBlur={handleBlur}
                        error={touched.gender && errors.gender}
                        // helperText={touched.gender && errors.gender}
                      >
                        <FormControlLabel
                          id="female"
                          value="female"
                          control={<Radio />}
                          label="Female"
                          color="primary"
                        />
                        <FormControlLabel
                          id="male"
                          value="male"
                          control={<Radio />}
                          label="Male"
                        />
                        {/* <FormControlLabel value="other" control={<Radio />} label="Other" /> */}
                      </RadioGroup>
                    </FormControl>
                  </Grid>
                  <Grid item sm={3} xs={11} className={classes.paddingRight_16}>
                    <TextField
                      fullWidth
                      id={"maritalStatus"}
                      select
                      label="MARITAL STATUS / વૈવાહિક સ્થિતિ"
                      value={values.maritalStatus}
                      onChange={handleChange}
                      onBlur={handleBlur}
                      error={!!touched.maritalStatus && !!errors.maritalStatus}
                      helperText={touched.maritalStatus && errors.maritalStatus}
                      SelectProps={{
                        native: true
                      }}
                    >
                      <option key={0} disabled selected value={undefined} />
                      {dropDownListData.maritalStatusList &&
                        dropDownListData.maritalStatusList.length &&
                        dropDownListData.maritalStatusList.map(option => (
                          <option key={option.Id} value={option.Id}>
                            {option.Name}
                          </option>
                        ))}
                    </TextField>
                  </Grid>
                  {this.props.values.maritalStatus !== "Married" ? null : (
                    <Grid
                      item
                      sm={3}
                      xs={11}
                      className={classes.paddingRight_16}
                    >
                      <MuiPickersUtilsProvider utils={DateFnsUtils}>
                        <KeyboardDatePicker
                          fullWidth
                          id="anniversaryDate"
                          format="MM/dd/yyyy"
                          label="ANNIVERSARY DATE / વર્ષગાંઠની તારીખ"
                          KeyboardButtonProps={{
                            "aria-label": "change date"
                          }}
                          value={values.anniversaryDate}
                          onChange={(event, value) => {
                            this.props.setFieldValue(
                              "anniversary",
                              new Date(value)
                            );
                          }}
                          onBlur={handleBlur}
                          error={
                            !!touched.anniversaryDate &&
                            !!errors.anniversaryDate
                          }
                          helperText={
                            touched.anniversaryDate && errors.anniversaryDate
                          }
                        />
                      </MuiPickersUtilsProvider>
                    </Grid>
                  )}
                </Grid>
              </Grid>
              <Grid
                item
                xs={12}
                style={{ paddingTop: "8px", paddingBottom: "8px" }}
              >
                <Grid
                  container
