      email: yup.string().required("Email Name is required").email("please enter valid email"),
      mobile: yup.string().required("Mobile No is required").matches(PhoneRegExp, "Mobile Number must be 10 digits long"),
      residencePhone: yup.string().matches(PhoneRegExp, "Phone Number must be 10 digits long"),
      officePhone: yup.string().matches(PhoneRegExp, "Phone Number must be 10 digits long"),
      nativePlaceId: yup.string().required("Please select Native Place"),
      occupationId: yup.string().required("Please select Occupation"),
      dob: yup.date().required("Date of Birth is required"),
      marriageDate: yup.date().required("Marriage Date is required"),
      motherName: yup.string().required("Mother Name is required").max(50, "Mother Name cannot exceed then 50 character"),
      motherNativePlaceId: yup.string().required("Please select Mother Native Place"),
      r_Address1: yup.string().required("Address1 is required"),
      r_Address2: yup.string(),
      r_Address3: yup.string(),
      r_ZipCode: yup.string().required("Zip Code is required").matches(ZipCodeRegExp, "Zip Code must be 10 digits long"),
      r_CountryId: yup.string().required("Country is required"),
      r_StateId: yup.string().required("State is required"),
      r_CityId: yup.string().required("City is required"),
      o_Address1: yup.string().required("Address1 is required"),
      o_Address2: yup.string(),
      o_Address3: yup.string(),
      o_ZipCode: yup.string().required("Zip Code is required").matches(ZipCodeRegExp, "Zip Code must be 10 digits long"),
      o_CountryId: yup.string().required("Country is required"),
      o_StateId: yup.string().required("State is required"),
      o_CityId: yup.string().required("City is required"),
      aadhaarNo: yup.string().matches(AdhharRegExp, "Aadhar no must be 12 digits long"),
      otherOccupation: document.getElementById("occupationId").value == "0" && yup.string().required("Other occupation is required")
    });
  },
  handleSubmit: (values, props) => {
    console.log(values, props);
    var dobOrg = getFormattedDate(values.dob);
    var marriageDateOrg = getFormattedDate(values.marriageDate);
    values.dob = getFormattedDate(values.dob);
    values.marriageDate = getFormattedDate(values.marriageDate);
    var valuesToSend = produce(values, draftState => {});
    props.props.saveFamilyDataToStore("family", valuesToSend).then(item => {
      props.props.tabHandleChange(null, 1);
    });
    props.setSubmitting(false); // setTimeout(() => {
    //   alert(JSON.stringify(values, null, 2));
    //   setSubmitting(false);
    // }, 1000);
    // dispatch(addProduct(values));
  },
  displayName: "BasicForm"
})(FamilyForm);

const getFormattedDate = dateToFomat => {
  var dd = dateToFomat.getDate();
  var mm = dateToFomat.getMonth() + 1;
  var yyyy = dateToFomat.getFullYear();

  if (dd < 10) {
    dd = "0" + dd;
  }

  if (mm < 10) {
    mm = "0" + mm;
  }

  return yyyy + "/" + mm + "/" + dd;
};

const mapStateToProps = state => {
  console.log(state);
  return {
    basicDetail: state.family.basicDetail,
    residenceDetail: state.family.residenceDetail,
    officeDetail: state.family.officeDetail,
    dropDownListData: state.dropDownList.dropDownListData ? state.dropDownList.dropDownListData : {}
  };
};

const mapDispatchToProps = dispatch => {
  return bindActionCreators({
    saveFamilyDataToStore,
    getAllDropDownListData,
    setWholeDataToStoreInitially
  }, dispatch);
};

export default withStyles(useStyles, {
  withTheme: true
})(connect(mapStateToProps, mapDispatchToProps)(EnhancedForm)); // export default withStyles(useStyles, { withTheme: true })(FamilyForm);

