import React, { Component, Fragment } from "react";
import { bindActionCreators } from "redux";
import { connect } from "react-redux";
import { withFormik } from "formik";
import Grid from "@material-ui/core/Grid";
import TextField from "@material-ui/core/TextField";
import DateFnsUtils from "@date-io/date-fns";
import {
  MuiPickersUtilsProvider,
  KeyboardDatePicker
} from "@material-ui/pickers";
import { Typography, Divider, Button, withStyles } from "@material-ui/core";
import * as yup from "yup";
import {
  saveFamilyDataToStore,
  getAllDropDownListData
} from "../action/familyAction";
import Checkbox from "@material-ui/core/Checkbox";
import FormControlLabel from "@material-ui/core/FormControlLabel";
import MemberForm from "./memberForm";
import {
  addUpdateMemberDataToDaughterMemberList,
  deleteMemberDataToDaughterMemberList,
  addUpdateDaughterMemberList,
  deleteDaughterMemberList
} from "../action/daughterAction";
import { PhoneRegExp, ZipCodeRegExp, AdhharRegExp } from "../utils/constants";

const useStyles = theme => ({
  titleContainer: {
    [theme.breakpoints.up("sm")]: {
      paddingLeft: theme.spacing(6),
      paddingRight: theme.spacing(5)
    },
    [theme.breakpoints.down("sm")]: {
      paddingLeft: theme.spacing(2),
      paddingRight: theme.spacing(2)
    },
    textAlign: "left"
  },

  titleContainer2: {
    [theme.breakpoints.down("up")]: {
      paddingLeft: theme.spacing(6),
      paddingRight: theme.spacing(3),
      textAlign: "left",
      marginTop: theme.spacing(3)
    },
    [theme.breakpoints.down("sm")]: {
      paddingLeft: theme.spacing(1),
      paddingRight: theme.spacing(2),
      textAlign: "left",
      marginTop: theme.spacing(2)
    }
  },
  row: {
    [theme.breakpoints.up("sm")]: {
      paddingLeft: theme.spacing(6),
      paddingRight: theme.spacing(6)
    }
  },
  paddingRight_16: {
    [theme.breakpoints.up("sm")]: {
      paddingRight: theme.spacing(2)
    }
  },
  paddingLeftRight_36: {
    [theme.breakpoints.up("sm")]: {
      paddingRight: theme.spacing(6),
      paddingLeft: theme.spacing(6)
    }
  }
});

class DaughterForm extends Component {
  constructor(props) {
    super(props);
    this.state = {
      selectedDate: new Date()
    };
  }

  componentDidMount() {
    this.props.getAllDropDownListData();
  }
  render() {
    console.log(this.props);
    const {
      tabHandleChange,
      dataObj,
      formIndex,
      dropDownListData,
      classes,
      theme,
      values,
      touched,
      errors,
      dirty,
      isSubmitting,
      handleChange,
      handleBlur,
      handleSubmit,
      handleReset,
      addUpdateMemberDataToDaughterMemberList,
      deleteMemberDataToDaughterMemberList,
      deleteDaughterMemberList
    } = this.props;
    const { selectedDate } = this.state;
    console.log(this.props);
    return (
      <Fragment>
        <Grid container alignItems="center">
          <Grid item xs={12}>
            <form autoComplete="off" onSubmit={handleSubmit}>
              <Grid container alignItems="center">
                <Grid item xs={12}>
                  <Grid container alignContent="center" alignItems="center">
                    <Grid item xs={12} className={classes.titleContainer}>
                      <Typography gutterBottom color="primary">
                        BASIC DETAIL /  
                      </Typography>
                      <Divider />
                    </Grid>
                    <Grid
                      item
                      xs={12}
                      style={{ paddingTop: "8px", paddingBottom: "8px" }}
                    >
                      <Grid
                        container
                        className={classes.row}
                        justify="space-evenly"
                      >
                        <Grid
                          item
                          sm={3}
                          xs={11}
                          className={classes.paddingRight_16}
                        >
                          <TextField
                            fullWidth
                            id="mobile"
                            label="MOBILE NUMBER /  "
                            value={values.mobile}
                            onChange={handleChange}
                            onBlur={handleBlur}
                            error={!!touched.mobile && !!errors.mobile}
                            helperText={touched.mobile && errors.mobile}
                          />
                        </Grid>
                        <Grid
                          item
                          sm={3}
                          xs={11}
                          className={classes.paddingRight_16}
                        >
                          <TextField
                            fullWidth
                            id="firstName"
                            label="FIRST NAME /  "
                            value={values.firstName}
                            onChange={handleChange}
                            onBlur={handleBlur}
                            error={!!touched.firstName && !!errors.firstName}
                            helperText={touched.firstName && errors.firstName}
                          />
                        </Grid>
                        <Grid
                          item
                          sm={3}
                          xs={11}
                          className={classes.paddingRight_16}
                        >
                          <TextField
                            fullWidth
                            id="middleName"
                            label="MIDDLE NAME /  "
                            value={values.middleName}
                            onChange={handleChange}
                            onBlur={handleBlur}
                            error={!!touched.middleName && !!errors.middleName}
                            helperText={touched.middleName && errors.middleName}
                          />
                        </Grid>
                        <Grid item sm={3} xs={11}>
                          <TextField
                            fullWidth
                            id="lastName"
                            label="LAST NAME /  "
                            value={values.lastName}
                            onChange={handleChange}
                            onBlur={handleBlur}
                            error={!!touched.lastName && !!errors.lastName}
                            helperText={touched.lastName && errors.lastName}
                          />
                        </Grid>
                      </Grid>
                    </Grid>
                    <Grid
                      item
                      xs={12}
                      style={{ paddingTop: "8px", paddingBottom: "8px" }}
                    >
                      <Grid
                        container
                        // spacing={3}
                        className={classes.row}
                        justify="space-evenly"
                      >
                        <Grid
                          item
                          sm={3}
                          xs={11}
                          className={classes.paddingRight_16}
                        >
                          <TextField
                            fullWidth
                            id="fatherName"
                            label="FATHER NAME /  "
                            value={values.fatherName}
                            onChange={handleChange}
                            onBlur={handleBlur}
                            error={!!touched.fatherName && !!errors.fatherName}
                            helperText={touched.fatherName && errors.fatherName}
                          />
                        </Grid>

                        <Grid
                          item
                          sm={3}
                          xs={11}
                          className={classes.paddingRight_16}
                        >
                          <TextField
                            fullWidth
                            id="motherName"
                            label="MOTHER NAME /  "
                            value={values.motherName}
                            onChange={handleChange}
                            onBlur={handleBlur}
                            error={!!touched.motherName && !!errors.motherName}
                            helperText={touched.motherName && errors.motherName}
                          />
                        </Grid>
                        <Grid
                          item
                          sm={3}
                          xs={11}
                          className={classes.paddingRight_16}
                        >
                          <TextField
                            fullWidth
                            id="nativePlaceId"
                            select
                            label="FATHER NATIVE PLACE /   "
                            value={values.nativePlaceId}
                            onChange={handleChange}
                            onBlur={handleBlur}
                            error={
                              !!touched.nativePlaceId && !!errors.nativePlaceId
                            }
                            helperText={
                              touched.nativePlaceId && errors.nativePlaceId
                            }
                            SelectProps={{
                              native: true
                            }}
                          >
                            <option key={0} disabled selected />
                            {dropDownListData.nativePlaceList &&
                              dropDownListData.nativePlaceList.length > 0 &&
                              dropDownListData.nativePlaceList.map(option => (
                                <option key={option.Id} value={option.Id}>
                                  {option.Name}
                                </option>
                              ))}
                          </TextField>
                        </Grid>
                        <Grid
                          item
                          sm={3}
                          xs={11}
                          className={classes.paddingRight_16}
                        >
                          <TextField
                            fullWidth
                            id="motherNativePlaceId"
                            select
                            label="FATHER PRESENT PLACE /   "
                            value={values.motherNativePlaceId}
                            onChange={handleChange}
                            onBlur={handleBlur}
                            error={
                              !!touched.motherNativePlaceId &&
                              !!errors.motherNativePlaceId
                            }
                            helperText={
                              touched.motherNativePlaceId &&
                              errors.motherNativePlaceId
                            }
                            SelectProps={{
                              native: true
                            }}
                          >
                            <option key={0} disabled selected />
                            {dropDownListData.nativePlaceList &&
                              dropDownListData.nativePlaceList.length > 0 &&
                              dropDownListData.nativePlaceList.map(option => (
                                <option key={option.Id} value={option.Id}>
                                  {option.Name}
                                </option>
                              ))}
                          </TextField>
                        </Grid>
                      </Grid>
                    </Grid>
                    {this.props.values.occupationId !== "0" ? null : (
                      <Grid
                        item
                        xs={12}
                        style={{ paddingTop: "8px", paddingBottom: "8px" }}
                      >
                        <Grid
                          container
                          alignItems="center"
                          style={{
                            paddingLeft: theme.spacing(6),
                            paddingRight: theme.spacing(6)
                          }}
                        >
                          <Grid
                            item
                            sm={3}
                            style={{ paddingLeft: "0px" }}
                            className={classes.paddingRight_16}
                            xs={11}
                          >
                            <TextField
                              id="otherOccupation"
                              label="Other Occupation"
                              fullWidth
                              value={values.otherOccupation}
                              onChange={handleChange}
                              onBlur={handleBlur}
                              error={
                                !!touched.otherOccupation &&
                                !!errors.otherOccupation
                              }
                              helperText={
                                touched.otherOccupation &&
                                errors.otherOccupation
                              }
                            />
                          </Grid>
                        </Grid>
                      </Grid>
                    )}

                    <Grid
                      item
                      xs={12}
                      style={{ paddingTop: "8px", paddingBottom: "8px" }}
                    >
                      <Grid
                        container
                        // spacing={3}
                        className={classes.row}
                        justify="space-evenly"
                      >
                        <Grid
                          item
                          sm={3}
                          xs={11}
                          className={classes.paddingRight_16}
                        >
                          <TextField
                            id="email"
                            label="EMAIL / "
                            fullWidth
                            value={values.email}
                            onChange={handleChange}
                            onBlur={handleBlur}
                            error={!!touched.email && !!errors.email}
                            helperText={touched.email && errors.email}
                          />
                        </Grid>
                        <Grid
                          item
                          sm={3}
                          xs={11}
                          className={classes.paddingRight_16}
