                              onChange={(event, value) => {
                                console.log(event, value);
                                this.props.setFieldValue(
                                  "dob",
                                  new Date(value)
                                );
                              }}
                              onBlur={handleBlur}
                              error={!!touched.dob && !!errors.dob}
                              helperText={touched.dob && errors.dob}
                              // value={new Date().getDate()}
                              // onChange={}
                            />
                          </MuiPickersUtilsProvider>
                        </Grid>

                        <Grid
                          item
                          sm={3}
                          xs={11}
                          className={classes.paddingRight_16}
                        >
                          <TextField
                            fullWidth
                            id={"maritalStatus"}
                            select
                            label="MARITAL STATUS /  "
                            value={values.maritalStatus}
                            onChange={handleChange}
                            onBlur={handleBlur}
                            error={
                              !!touched.maritalStatus && !!errors.maritalStatus
                            }
                            helperText={
                              touched.maritalStatus && errors.maritalStatus
                            }
                            SelectProps={{
                              native: true
                            }}
                          >
                            <option
                              key={0}
                              disabled
                              selected
                              value={undefined}
                            />
                            {dropDownListData.maritalStatusList &&
                              dropDownListData.maritalStatusList.length &&
                              dropDownListData.maritalStatusList.map(option => (
                                <option key={option.Id} value={option.Id}>
                                  {option.Name}
                                </option>
                              ))}
                          </TextField>

                        </Grid>


                        {(values.maritalStatus == "SINGLE") ||
                        (values.maritalStatus == "WIDOW") ? null : (
                           <span>{values.maritalStatus}</span>   
                          )
                         }
                      </Grid>
                    </Grid>
                    <Grid
                      item
                      xs={12}
                      style={{ paddingTop: "8px", paddingBottom: "8px" }}
                    >
                      <Grid container className={classes.row}>
                        <Grid item sm={3} xs={11}>
                          <TextField
                            fullWidth
                            id="residencePhone"
                            label="RESIDENCE PHONE /  "
                            value={values.residencePhone}
                            onChange={handleChange}
                            onBlur={handleBlur}
                            error={
                              !!touched.residencePhone &&
                              !!errors.residencePhone
                            }
                            helperText={
                              touched.residencePhone && errors.residencePhone
                            }
                          />
                        </Grid>
                        <Grid
                          item
                          sm={3}
                          xs={11}
                          className={classes.paddingRight_16}
                        >
                          <TextField
                            fullWidth
                            id="officePhone"
                            label="OFFICE PHONE / . "
                            value={values.officePhone}
                            onChange={handleChange}
                            onBlur={handleBlur}
                            error={
                              !!touched.officePhone && !!errors.officePhone
                            }
                            helperText={
                              touched.officePhone && errors.officePhone
                            }
                          />
                        </Grid>
                        <Grid
                          item
                          sm={3}
                          xs={11}
                          className={classes.paddingRight_16}
                        >
                          <TextField
                            fullWidth
                            id="aadhaarNo"
                            label="AADHAAR NO /  "
                            value={values.aadhaarNo}
                            onChange={handleChange}
                            onBlur={handleBlur}
                            error={!!touched.aadhaarNo && !!errors.aadhaarNo}
                            helperText={touched.aadhaarNo && errors.aadhaarNo}
                          />
                        </Grid>
                        <Grid
                          item
                          sm={3}
                          xs={11}
                          className={classes.paddingRight_16}
                        >
                          <TextField
                            fullWidth
                            id="bloodGroup"
                            select
                            label="BLOOD GROUP /  "
                            value={values.bloodGroup}
                            onChange={handleChange}
                            onBlur={handleBlur}
                            error={!!touched.bloodGroup && !!errors.bloodGroup}
                            helperText={touched.bloodGroup && errors.bloodGroup}
                            SelectProps={{
                              native: true
                            }}
                          >
                            <option key={0} disabled selected />
                            {dropDownListData.bloodGroupList &&
                              dropDownListData.bloodGroupList.length &&
                              dropDownListData.bloodGroupList.map(option => (
                                <option key={option.Id} value={option.Id}>
                                  {option.Name}
                                </option>
                              ))}
                          </TextField>
                        </Grid>
                      </Grid>
                    </Grid>
                    <Grid
                      item
                      xs={12}
                      style={{ paddingTop: "8px", paddingBottom: "8px" }}
                    >
                      <Grid
                        container
                        alignItems="center"
                        style={{
                          paddingLeft: theme.spacing(6),
                          paddingRight: theme.spacing(6)
                        }}
                      >
                        <Grid
                          item
                          sm={3}
                          xs={11}
                          className={classes.paddingRight_16}
                        >
                          <TextField
                            fullWidth
                            id="zodiac"
                            select
                            label="ZODIAC / "
                            value={values.zodiac}
                            onChange={handleChange}
                            onBlur={handleBlur}
                            error={!!touched.zodiac && !!errors.zodiac}
                            helperText={touched.zodiac && errors.zodiac}
                            SelectProps={{
                              native: true
                            }}
                          >
                            <option key={0} disabled selected />
                            {dropDownListData.zodiacList &&
                              dropDownListData.zodiacList.length &&
                              dropDownListData.zodiacList.map(option => (
                                <option key={option.Id} value={option.Id}>
                                  {option.Name}
                                </option>
                              ))}
                          </TextField>
                        </Grid>
                      </Grid>
                    </Grid>
                  </Grid>
                </Grid>

                <Grid item xs={12} style={{ paddingTop: theme.spacing(2) }}>
                  <Grid container alignItems="center">
                    <Grid item xs={12} className={classes.titleContainer2}>
                      <Typography gutterBottom color="primary">
                        RESIDENCE ADDRESS /  
                      </Typography>
                      <Divider />
                    </Grid>
                    <Grid
                      item
                      xs={12}
                      style={{ paddingTop: "8px", paddingBottom: "8px" }}
                    >
                      <Grid
                        container
                        alignItems="center"
                        style={{
                          paddingLeft: theme.spacing(6),
                          paddingRight: theme.spacing(6)
