import React, { Component } from "react";
import { withFormik } from "formik";
import * as yup from "yup";
import TextField from "@material-ui/core/TextField";
import DateFnsUtils from "@date-io/date-fns";
import {
  MuiPickersUtilsProvider,
  KeyboardDatePicker
} from "@material-ui/pickers";
import Radio from "@material-ui/core/Radio";
import RadioGroup from "@material-ui/core/RadioGroup";
import FormControlLabel from "@material-ui/core/FormControlLabel";
import FormControl from "@material-ui/core/FormControl";
import FormLabel from "@material-ui/core/FormLabel";
import Checkbox from "@material-ui/core/Checkbox";
import Divider from "@material-ui/core/Divider";
import Grid from "@material-ui/core/Grid";
import Button from "@material-ui/core/Button";
import Dialog from "@material-ui/core/Dialog";
import DialogActions from "@material-ui/core/DialogActions";
import DialogContent from "@material-ui/core/DialogContent";
import DialogTitle from "@material-ui/core/DialogTitle";
import Typography from "@material-ui/core/Typography";
import { withStyles } from "@material-ui/core/styles";
import { PhoneRegExp, ZipCodeRegExp, AdhharRegExp } from "../utils/constants";

const styles = theme => ({
  dialogTitle: { borderBottom: "1px solid gray", paddingBottom: "5px" },
  dialogBody: {},
  titleContainer: {
    [theme.breakpoints.up("sm")]: {
      paddingLeft: theme.spacing(6),
      paddingRight: theme.spacing(5)
    },
    [theme.breakpoints.down("sm")]: {
      paddingLeft: theme.spacing(2),
      paddingRight: theme.spacing(2)
    },
    textAlign: "left"
  },
  row: {
    [theme.breakpoints.up("sm")]: {
      paddingLeft: theme.spacing(6),
      paddingRight: theme.spacing(6)
    }
  },
  paddingRight_16: {
    [theme.breakpoints.up("sm")]: {
      paddingRight: theme.spacing(2)
    }
  }
});

class MemberModel extends Component {
  render() {

    const {
      parentIndex,
      dataObj,
      formIndex,
      memberArrayLength,
      dropDownListData,
      toggleMemberModel,
      toggleStatus,
      classes,
      theme,
      values,
      touched,
      errors,
      dirty,
      isSubmitting,
      handleChange,
      handleBlur,
      handleSubmit
    } = this.props;
    console.log(values, this.props);
    return (
      <Dialog
        open={toggleStatus}
        onClose={toggleMemberModel}
        aria-labelledby="alert-dialog-title"
        aria-describedby="alert-dialog-description"
        maxWidth="md"
        fullWidth
        disableBackdropClick={true}
        disableEscapeKeyDown={true}
        classes={{ paper: classes.dialogBody }}
      >
        <form
          autoComplete="off"
          onSubmit={handleSubmit}
          id={`form_${formIndex}`}
        >
          <DialogTitle id="alert-dialog-title">
            <Typography color="primary">Members Details</Typography>
          </DialogTitle>
          <DialogContent
            style={{
              borderTop: `1px solid ${theme.palette.grey["A100"]}`,
              borderBottom: `1px solid ${theme.palette.grey["A100"]}`
            }}
          >
            <Grid container alignItems="center">
              <Grid item xs={12}>
                <Grid container alignItems="center">
                  <Grid
                    item
                    xs={12}
                    style={{ paddingTop: "8px", paddingBottom: "8px" }}
                  >
                    <Grid
                      container
                      className={classes.row}
                      justify="space-evenly"
                    >
                      <Grid
                        item
                        sm={3}
                        xs={11}
                        className={classes.paddingRight_16}
                      >
                       <TextField
                      fullWidth
                      id="firstName"
                      label="FIRST NAME / પ્રથમ નામ"
                       autoComplete="1"
                      value={values.firstName}
                      onChange={handleChange}
                      onBlur={handleBlur}
                      error={!!touched.firstName && !!errors.firstName}
                      helperText={touched.firstName && errors.firstName}
                    />
                      </Grid>
                      <Grid
                        item
                        sm={3}
                        xs={11}
                        className={classes.paddingRight_16}
                      >
                         <TextField
                      fullWidth
                      id="middleName"
                      label="MIDDLE NAME / પિતાનું નામ"
                      autoComplete="1"
                      value={values.middleName}
                      onChange={handleChange}
                      onBlur={handleBlur}
                      error={!!touched.middleName && !!errors.middleName}
                      helperText={touched.middleName && errors.middleName}
                    />
                      </Grid>
                      <Grid
                        item
                        sm={3}
                        xs={11}
                        className={classes.paddingRight_16}
                      >
                       <TextField
                      fullWidth
                      id="lastName"
                      label="LAST NAME / છેલ્લું નામ"
                      autoComplete="1"
                      value={values.lastName}
                      onChange={handleChange}
                      onBlur={handleBlur}
                      error={!!touched.lastName && !!errors.lastName}
                      helperText={touched.lastName && errors.lastName}
                    />
                      </Grid>
                      <Grid item sm={3} xs={11}>
                      <TextField
                      fullWidth
                      id="relation"
                      select
                      label="RELATION / સંબંધ"
                      autoComplete="1"
                      value={values.relation}
                      onChange={handleChange}
                      onBlur={handleBlur}
                      error={!!touched.relation && !!errors.relation}
                      helperText={touched.relation && errors.relation}
                      SelectProps={{
                        native: true
                      }}
                    >
                          <option key={0} disabled selected />
                          {dropDownListData.relationList &&
                            dropDownListData.relationList.length &&
                            dropDownListData.relationList.map(option => (
                              <option key={option.Id} value={option.Id}>
                                {option.Name}
                              </option>
                            ))}
                        </TextField>
                      </Grid>
                    </Grid>
                  </Grid>
                  <Grid
                    item
                    xs={12}
                    style={{ paddingTop: "8px", paddingBottom: "8px" }}
                  >
                    <Grid container className={classes.row}>
                      <Grid
                        item
                        sm={3}
                        xs={11}
                        className={classes.paddingRight_16}
                      >
                        <MuiPickersUtilsProvider utils={DateFnsUtils}>
                      <KeyboardDatePicker
                        fullWidth
                        id="dateOfBirth"
                        format="MM/dd/yyyy"
                        label="DATE OF BIRTH / જન્મ તારીખ"
                        autoComplete="1"
                        KeyboardButtonProps={{
                          "aria-label": "change date"
                        }}
                        value={values.dateOfBirth}
                        onChange={(event, value) => {
                          this.props.setFieldValue(
                            "dateOfBirth",
                            new Date(value)
                          );
                        }}
                        onBlur={handleBlur}
                        error={!!touched.dateOfBirth && !!errors.dateOfBirth}
                        helperText={touched.dateOfBirth && errors.dateOfBirth}
                      />
                    </MuiPickersUtilsProvider>
                      </Grid>
                      <Grid
                        item
                        sm={3}
                        xs={11}
                        className={classes.paddingRight_16}
                      >
                        <FormControl
                          required
                          style={{ alignItems: "flex-start", float: "left" }}
                        >
                          <FormLabel>Gender</FormLabel>
                          {/* import FormHelperText from '@material-ui/core/FormHelperText'; */}
                          <RadioGroup
                            aria-label="GENDER / લિંગ"
                            id="gender"
                            style={{ flexDirection: "row" }}
                            autoComplete="1"
                            value={values.gender}
                            onChange={eve => {
                              this.props.setFieldValue(
                                "gender",
                                eve.target.value
                              );
                            }}
                            // onBlur={handleBlur}
                            error={touched.gender && errors.gender}
                            // helperText={touched.gender && errors.gender}
                          >
                            <FormControlLabel
                              id="female"
                              value="female"
                              control={<Radio />}
                              label="Female"
                              color="primary"
                            />
                            <FormControlLabel
                              id="male"
                              value="male"
                              control={<Radio />}
                              label="Male"
                            />
                            {/* <FormControlLabel value="other" control={<Radio />} label="Other" /> */}
                          </RadioGroup>
                        </FormControl>
                      </Grid>
                      <Grid
                        item
                        sm={3}
                        xs={11}
                        className={classes.paddingRight_16}
                      >
                        <TextField
                          fullWidth
                          id={"maritalStatus"}
                          select
                          label="MARITAL STATUS / વૈવાહિક સ્થિતિ"
                          autoComplete="1"
                          value={values.maritalStatus}
                          onChange={handleChange}
                          onBlur={handleBlur}
                          error={
                            !!touched.maritalStatus && !!errors.maritalStatus
                          }
                          helperText={
                            touched.maritalStatus && errors.maritalStatus
                          }
                          SelectProps={{
                            native: true
                          }}
                        >
                          <option key={0} disabled selected value={undefined} />
                          {dropDownListData.maritalStatusList &&
                            dropDownListData.maritalStatusList.length &&
                            dropDownListData.maritalStatusList.map(option => (
                              <option key={option.Id} value={option.Id}>
                                {option.Name}
                              </option>
                            ))}
                        </TextField>
                      </Grid>
                      {(this.props.values.maritalStatus == "SINGLE") ||
