import { axiosInstance } from "../api/axiosConfig";
import {
  SAVE_FAMILY_DATA_TO_STORE,
  SAVE_DROPDOWN_LIST_DATA_TO_STORE,
  ADD_DAUGHTER_MEMBER_LIST,
  ADD_MEMBER_DATA_TO_MEMBER_LIST,
  UPDATE_FAMILYID_TO_STORE
} from "./actionTypes";
