        url: "getFamilyFormDetail",
        method: "post"
      });
      if (response.status === 200) {
        if (response.data.length > 1 && response.data[0]) {
          // console.log(response);
          let data = jsonParseMethod(response.data[0][0]);
          // console.log(data);
          dispatch({
            type: SAVE_DROPDOWN_LIST_DATA_TO_STORE,
            payload: { data }
          });
